USE [SabinIO.Performance.SetRowBased]

SET NOCOUNT ON

--create a tally table
--no access to system tables required
CREATE TABLE dbo.Tally (
	N INT
	,CONSTRAINT PK_Tally_N PRIMARY KEY CLUSTERED (N)
	)
GO

DECLARE @Counter INT = 1

WHILE @Counter <= 12000
BEGIN
	INSERT INTO dbo.Tally (N)
	VALUES (@Counter)

	SET @Counter = @Counter + 1
END

SELECT COUNT(*)
FROM dbo.Tally

DROP TABLE dbo.Tally

--next method is a faster method to create a tally table, but requires access to master.sys tables
SELECT TOP 12000 IDENTITY(INT, 1, 1) AS N
INTO dbo.Tally
FROM Master.dbo.SysColumns sc1
	,Master.dbo.SysColumns sc2

ALTER TABLE dbo.Tally ADD CONSTRAINT PK_Tally_N PRIMARY KEY CLUSTERED (N)
	WITH FILLFACTOR = 100

GRANT SELECT
	,REFERENCES
	ON dbo.Tally
	TO PUBLIC
GO

--how do we count from 1 to 10 normally? In a loop
--row based
--this method creates 10 selects, ten result sets
DECLARE @i INT = 1

WHILE @i <= 10
BEGIN
	SELECT @i

	SET @i = @i + 1
END

--displaying count using tally table
--set based
--this method is one select, one resultset
SELECT N
FROM dbo.Tally
WHERE N <= 10
ORDER BY N

--find character position for each character
--using a loop, so row based
DECLARE @ElementsParam VARCHAR(8000)

SET @ElementsParam = ',Hydrogen,Helium,Lithium,Berylium,Boron,Carbon, Nitrogen,Oxygen,Fluorine,Neon,'

DECLARE @n INT = 1

WHILE @n <= LEN(@ElementsParam)
BEGIN
	SELECT @n
		,SUBSTRING(@ElementsParam, @n, 1)

	SET @n = @n + 1
END
GO

--replacing the loop by using the tally table, so set based
DECLARE @ElementsParam VARCHAR(8000)

SET @ElementsParam = ',Hydrogen,Helium,Lithium,Berylium,Boron,Carbon, Nitrogen,Oxygen,Fluorine,Neon,'

SELECT N
	,SUBSTRING(@ElementsParam, N, 1)
FROM dbo.Tally
WHERE N <= LEN(@ElementsParam)
ORDER BY N
GO

