	Number INT IDENTITY(1, 1)
	,Value VARCHAR(8000)
	)
DECLARE @N INT

SET @ElementsParam = ',' + @ElementsParam + ','
SET @N = 1
SET @StartTime = GETDATE()

WHILE @N < LEN(@ElementsParam)
BEGIN
	INSERT INTO @Elements
	VALUES (SUBSTRING(@ElementsParam, @N + 1, CHARINDEX(',', @ElementsParam, @N + 1) - @N - 1))

	SELECT @N = CHARINDEX(',', @ElementsParam, @N + 1)
END

SET @EndTime = GETDATE()

SELECT DATEDIFF(MILLISECOND, @StartTime, @EndTime) AS Duration

SELECT *
FROM @Elements
