DECLARE @Elements TABLE (
	Number INT IDENTITY(1, 1)
	,Value VARCHAR(8000)
	)

SET @ElementsParam = ',' + @ElementsParam + ','
SET @StartTime = GETDATE()

INSERT INTO @Elements (Value)
SELECT SUBSTRING(@ElementsParam, N + 1, CHARINDEX(',', @ElementsParam, N + 1) - N - 1)
FROM dbo.Tally
WHERE N < LEN(@ElementsParam)
	AND SUBSTRING(@ElementsParam, N, 1) = ','

SET @EndTime = GETDATE()

SELECT DATEDIFF(MILLISECOND, @StartTime, @EndTime) AS Duration

SELECT *
FROM @Elements

--the set based method is faster than row based, even at < 1000 rows
